import React from 'react';
import { useSelector } from 'react-redux';
import { selectCard } from '../reducers';

function Card({ index, onClick }) {
  const { name, isOpen, isMatched } = useSelector(state => selectCard(state, index));

  return (
    <div className={`card ${isOpen ? 'open' : ''} ${isMatched ? 'matched' : ''}`} onClick={onClick}>
      <div className="front">?</div>
      <div className="back">
        <img src={`https://raw.githubusercontent.com/samiheikki/javascript-guessing-game/master/static/logos/${name}.png`} alt={name} />
      </div>
    </div>
  );
}

export default Card;
