import { FLIP_CARD, MATCH_CARDS, RESET_GAME, SET_SCORE } from '../reducers/types';

export const flipCard = (name, index) => ({
  type: FLIP_CARD,
  payload: { name, index },
});

export const matchCards = () => ({
  type: MATCH_CARDS,
});

export const resetGame = () => ({
  type: RESET_GAME,
});

export const setScore = (score) => ({
  type: SET_SCORE,
  payload: score,
});
