import React from 'react';
import { connect } from 'react-redux';
import { flipCard, checkMatch } from '../actions';
import { selectOpenedCards, selectScore } from '../reducers/selectors';

class PlayGround extends React.Component {
    state = {
        frameworks: ['angular2', 'vue', 'react', 'grunt', 'phantomjs', 'ember', 'babel', 'ionic', 'gulp', 'meteor', 'yeoman', 'yarn', 'nodejs', 'bower', 'browserify'],
        duplicatedFrameworks: [],
        randomizedFrameworks: [],
        openedFrameworks: [],
        score: 0
      
    };

  constructor(props) {
    super(props);
    this.state = {
      frameworks: ['angular2','vue','react','grunt','phantomjs','ember','babel','ionic','gulp','meteor','yeoman','yarn','nodejs','bower','browserify'],
      duplicatedFrameworks: [],
      randomizedFrameworks: [],
    };
    this.start();
  }

  start() {
    const { frameworks } = this.state;
    const duplicatedFrameworks = frameworks.concat(frameworks);
    const randomizedFrameworks = this.shuffle(duplicatedFrameworks);
    this.setState({
      duplicatedFrameworks,
      randomizedFrameworks,
    });
  }

  shuffle(array) {
    let currentIndex = array.length, temporaryValue, randomIndex;
    while (0 !== currentIndex) {
      randomIndex = Math.floor(Math.random() * currentIndex);
      currentIndex -= 1;
      temporaryValue = array[currentIndex];
      array[currentIndex] = array[randomIndex];
      array[randomIndex] = temporaryValue;
    }
    return array;
  }

  handleClick(name, index) {
    const { openedFrameworks, flipCard, checkMatch } = this.props;
    if (openedFrameworks.length === 2) {
      setTimeout(() => {
        checkMatch();
      }, 750);
    } else {
      flipCard({ name, index });
      if (openedFrameworks.length === 2) {
        setTimeout(() => {
          checkMatch();
        }, 750);
      }
    }
  }

  render() {
    const { randomizedFrameworks } = this.state;
    const { openedFrameworks } = this.props;

    return (
      <div className="playground">
        {randomizedFrameworks.map((name, index) => (
          <Card key={index} framework={name} index={index} onClick={() => this.handleClick(name, index)} isOpen={openedFrameworks.includes(index)} />
        ))}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  openedFrameworks: selectOpenedCards(state),
  score: selectScore(state),
});

const mapDispatchToProps = {
  flipCard,
  checkMatch,
};

export default connect(mapStateToProps, mapDispatchToProps)(PlayGround);

class Card extends React.Component {
  render() {
    const { framework, isOpen, onClick } = this.props;
    return (
      <div className={`card ${isOpen ? 'opened' : ''}`} onClick={onClick}>
        <div className="front">
          ?
        </div>
        <div className="back">
          <img src={`https://raw.githubusercontent.com/samiheikki/javascript-guessing-game/master/static/logos/${framework}.png`} alt={framework} />
        </div>
      </div>
    );
  }
}
