import { FLIP_CARD, MATCH_CARDS, RESET_GAME, SET_SCORE } from './types';

const initialState = {
  finalizedFrameworks: [], // Kartlar burada saklanacak
  openedFrameworks: [], // Açık kartlar burada saklanacak
  score: 0, // Puan
};

const gameReducer = (state = initialState, action) => {
  switch (action.type) {
    case FLIP_CARD:
      return {
        ...state,
        openedFrameworks: [...state.openedFrameworks, action.payload],
        finalizedFrameworks: state.finalizedFrameworks.map((framework, index) =>
          index === action.payload.index ? { ...framework, close: false } : framework
        ),
      };

    case MATCH_CARDS:
      const [firstCard, secondCard] = state.openedFrameworks;
      const finalizedFrameworks = state.finalizedFrameworks.map((framework, index) =>
        index === firstCard.index || index === secondCard.index
          ? { ...framework, complete: true }
          : framework
      );

      return {
        ...state,
        finalizedFrameworks,
        openedFrameworks: [],
        score: state.score + 50,
      };

    case RESET_GAME:
      return initialState;

    case SET_SCORE:
      return {
        ...state,
        score: action.payload,
      };

    default:
      return state;
  }
};

export default gameReducer;
