import * as actionTypes from '../reducers/types';

export const initializeGame = () => ({ type: actionTypes.INITIALIZE_GAME });
export const flipCard = ({ name, index }) => ({ type: actionTypes.FLIP_CARD, payload: { name, index } });
export const checkMatch = () => ({ type: actionTypes.CHECK_MATCH });
export const restartGame = () => ({ type: actionTypes.RESTART_GAME });
