import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Card from './Card';
import { initializeGame, selectOpenedCards, selectScore, flipCard, checkMatch, restartGame } from '../actions';

function PlayGround() {
  const dispatch = useDispatch();
  const openedCards = useSelector(selectOpenedCards);
  const score = useSelector(selectScore);

  useEffect(() => {
    dispatch(initializeGame());
  }, [dispatch]);

  const handleClick = (name, index) => {
    if (openedCards.length === 2) {
      setTimeout(() => {
        dispatch(checkMatch());
      }, 750);
    } else {
      dispatch(flipCard({ name, index }));
    }
  };

  const handleRestart = () => {
    dispatch(restartGame());
  };

  return (
    <div className="playground">
      <h2>Score: {score}</h2>
      <div className="cards">
        <div className="row">
          {[...Array(5)].map((_, rowIndex) => (
            <div key={rowIndex} className="card-row">
              {[...Array(5)].map((_, colIndex) => {
                const cardIndex = rowIndex * 5 + colIndex;
                return (
                  <Card
                    key={cardIndex}
                    index={cardIndex}
                    onClick={() => handleClick(cardIndex)}
                  />
                );
              })}
            </div>
          ))}
        </div>
      </div>
      <button onClick={handleRestart}>Yeniden Oyna</button>
    </div>
  );
}

export default PlayGround;
