export const initializeGame = () => ({ type: 'INITIALIZE_GAME' });
export const flipCard = ({ name, index }) => ({ type: 'FLIP_CARD', payload: { name, index } });
export const checkMatch = () => ({ type: 'CHECK_MATCH' });
export const restartGame = () => ({ type: 'RESTART_GAME' });
