const initialState = {
    cards: [],
    openedCards: [],
    score: 0,
  };
  
  const gameReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'INITIALIZE_GAME':
        // Implement initialize game logic here
        return {
          ...state,
          cards: [], // initialize cards array
          openedCards: [],
          score: 0,
        };
      case 'FLIP_CARD':
        // Implement flip card logic here
        return state;
      case 'CHECK_MATCH':
        // Implement check match logic here
        return state;
      case 'RESTART_GAME':
        // Implement restart game logic here
        return state;
      default:
        return state;
    }
  };
  
  export default gameReducer;
