import React from 'react';
import { connect } from 'react-redux';
import { flipCard } from '../actions';

class Card extends React.Component {
  handleClick = () => {
    const { framework, index, close, flipCard } = this.props;
    if (close) {
      flipCard(framework, index);
    }
  };

  render() {
    const { close, complete, framework } = this.props;

    return (
      <div className={`card ${!close ? 'opened' : ''} ${complete ? 'matched' : ''}`} onClick={this.handleClick}>
        <div className="front">?</div>
        <div className="back">
          <img src={`https://raw.githubusercontent.com/samiheikki/javascript-guessing-game/master/static/logos/${framework}.png`} alt={framework} />
        </div>
      </div>
    );
  }
}

export default connect(null, { flipCard })(Card);
